mapper.select:
SELECT node_id as id, node_name as name, TRUE as directory, node_path as fullPath, 0 as isShare, "" as size, update_time updateTime FROM resourcePath
WHERE is_frist=1
WHERE father_node=#{path}
UNION SELECT resource_id as id, resource_name as name, FALSE as directory, full_path as fullPath , 0 as isShare, size as size, update_time updateTime FROM resource WHERE path=#{path} and status=1
UNION SELECT share_id as id, r.resource_name as name, FALSE as directory, r.full_path as fullPath, r.size as size, 1 as isShare, s.update_time updateTime FROM share s LEFT JOIN resource r on s.resource_id=r.resource_id
WHERE s.user_id=#{userId}
WHERE 1=1
and invented_path=#{path}
